import React, { useState } from 'react';
import Chart from 'chart.js/auto';
import './chart.css.css'; // Import CSS file for styling

const Graph = () => {
    const [xAxis, setXAxis] = useState('');
    const [yAxis, setYAxis] = useState('');
    const [chart, setChart] = useState(null);

    const plotGraph = () => {
        const xData = xAxis.split(',').map(Number);
        const yData = yAxis.split(',').map(Number);

        if (chart) {
            chart.destroy();
        }

        const ctx = document.getElementById('myChart').getContext('2d');
        const newChart = new Chart(ctx, {
            type: 'line',
            data: {
                labels: xData,
                datasets: [{
                    label: 'Data',
                    data: yData,
                    borderColor: '#007bff',
                    borderWidth: 2,
                    pointBackgroundColor: '#007bff',
                    tension: 0.2
                }]
            },
            options: {
                scales: {
                    x: {
                        beginAtZero: true
                    },
                    y: {
                        beginAtZero: true
                    }
                },
                plugins: {
                    legend: {
                        display: false
                    }
                }
            }
        });

        setChart(newChart);
    };

    return (
        <div className="graph-container">
            <h2 className="graph-heading">Graph Plotter</h2>
            <div className="form-group">
                <label htmlFor="x-axis">X-axis Data (comma separated):</label>
                <input
                    type="text"
                    id="x-axis"
                    value={xAxis}
                    onChange={(e) => setXAxis(e.target.value)}
                    placeholder="e.g., 1,2,3,4,5"
                />
            </div>
            <div className="form-group">
                <label htmlFor="y-axis">Y-axis Data (comma separated):</label>
                <input
                    type="text"
                    id="y-axis"
                    value={yAxis}
                    onChange={(e) => setYAxis(e.target.value)}
                    placeholder="e.g., 10,20,30,40,50"
                />
            </div>
            <button className="plot-btn" onClick={plotGraph}>Plot Graph</button>
            <div className="chart-container">
                <canvas id="myChart" width="400" height="200"></canvas>
            </div>
        </div>
    );
};

export default Graph;
